mapper.select:
SELECT count(*) FROM board_tb
WHERE title LIKE '%'||#{value}||'%'
WHERE mb_id = #{value}
WHERE category = #{value}

SELECT c.id, c.writer, c.category, c.title, c.content, c.reg_date, count(like_tb.id) AS like_count, reply_count FROM (SELECT b.id, b.category, b.writer, b.title, b.content, b.reg_date, count(reply_tb.id) AS reply_count FROM (SELECT rownum AS rnum, a.id, a.category, a.writer, a.title, a.content, a.reg_date FROM (SELECT id, category, member_tb.mb_nickname AS writer, title, content, reg_date FROM board_tb JOIN member_tb ON board_tb.mb_id = member_tb.mb_id
WHERE title LIKE '%'||#{query}||'%'
WHERE board_tb.mb_id = #{memberId}
WHERE category = #{category}
)a WHERE rownum <= #{page} * #{countList} ) b LEFT JOIN reply_tb ON b.id = reply_tb.board_id where b.rnum >= (#{page}-1) * #{countList}+1 GROUP BY b.id, b.writer, b.category, b.title, b.content, b.reg_date)c LEFT JOIN like_tb ON c.id = like_tb.board_id GROUP BY c.id, c.writer, c.category, c.title, c.content, c.reg_date, reply_count ORDER BY c.id DESC
